import React, { useState } from 'react';
import './login.css';
import axios from 'axios';
import { GoogleLogin } from 'react-google-login';
import user_icon from '../assets/person.png';
import email_icon from '../assets/email.png';
import password_icon from '../assets/password.png';
import { useNavigate } from "react-router-dom";

const LoginSignup = () => {
  const [action, setAction] = useState("Login");
  const navigate = useNavigate();
  const [data, setData] = useState({
    username: '',
    email: '',
    password: ''
  });

  const handleInput = (event) => {
    setData({ ...data, [event.target.name]: event.target.value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (action === "Login") {
      axios.post('http://localhost:8000/login', { email: data.email, password: data.password })
        .then(res => {
          console.log(res.data);
          navigate("/dashboard");
        })
        .catch(err => {
          if (err.response) {
            alert(err.response.data.message || "Login failed. Please try again!");
          }
        });
    } else {
      axios.post('http://localhost:8000/register', { email: data.email, password: data.password, username: data.username })
        .then(res => {
          console.log(res.data);
          alert("Registration Successful!");
          setData({ username: "", email: "", password: "" });
          setAction("Login");
        })
        .catch(err => {
          if (err.response) {
            alert(err.response.data.message || "Registration failed. Please try again!");
          }
        });
    }
  };

  const handleGoogleSuccess = async (response) => {
    console.log("Google Login Success: ", response);
    axios.post('http://localhost:8000/auth/google/callback', { tokenId: response.tokenId })
      .then(res => {
        console.log(res.data);
        navigate("/dashboard");
      })
      .catch(err => {
        alert("Google Login failed. Please try again!");
      });
  };

  const handleGoogleFailure = (error) => {
    console.log("Google Login Failed: ", error);
    alert("Google Login Failed. Please try again!");
  };

  return (
    <div className="container">
      <div className="header">
        <div className="text">{action}</div>
        <div className="underline"></div>
      </div>
      <form onSubmit={handleSubmit}>
        <div className="inputs">
          {action === "Login" ? null : (
            <div className="input">
              <img src={user_icon} alt="" />
              <input type="text" placeholder='Username' onChange={handleInput} name='username' value={data.username} />
            </div>
          )}
          <div className="input">
            <img src={email_icon} alt="" />
            <input type="email" placeholder='Email ID' onChange={handleInput} name='email' value={data.email} />
          </div>
          <div className="input">
            <img src={password_icon} alt="" />
            <input type="password" placeholder='Password' onChange={handleInput} name='password' value={data.password} />
          </div>
        </div>
        <div className="action-submit">
          <button type='submit' className='submit-button'>Submit</button>
        </div>
        <div className="google-login">
          <GoogleLogin
            clientId={process.env.REACT_APP_GOOGLE_CLIENT_ID}
            buttonText="Sign in with Google"
            onSuccess={handleGoogleSuccess}
            onFailure={handleGoogleFailure}
            cookiePolicy={'single_host_origin'}
          />
        </div>
        <div className="submit-container">
          <div className={action === "Login" ? "submit gray" : "submit"} onClick={() => { setAction("Register") }}>Register</div>
          <div className={action === "Register" ? "submit gray" : "submit"} onClick={() => { setAction("Login") }}>Login</div>
        </div>
      </form>
    </div>
  );
};

export default LoginSignup;
